function svgDataURI(bgColor, label) {
  const svg = `<svg xmlns='http://www.w3.org/2000/svg' width='600' height='400'><rect width='100%' height='100%' rx='12' fill='${bgColor}'/><text x='50%' y='50%' font-size='32' font-family='Arial' fill='#444' text-anchor='middle' dominant-baseline='middle'>${label}</text></svg>`;
  return 'data:image/svg+xml;utf8,' + encodeURIComponent(svg);
}


function getCart() {
  try { return JSON.parse(localStorage.getItem('cart') || '{}'); } catch (e) { return {}; }
}
function saveCart(cart) { localStorage.setItem('cart', JSON.stringify(cart)); }
function clearCart() { localStorage.removeItem('cart'); }


function addToCart(productId, qty = 1) {
  const cart = getCart();
  cart[productId] = (cart[productId] || 0) + qty;
  saveCart(cart);
}

function setCartQty(productId, qty) {
  const cart = getCart();
  if (qty <= 0) {
    delete cart[productId];
  } else {
    cart[productId] = qty;
  }
  saveCart(cart);
}

function removeFromCart(productId) {
  const cart = getCart();
  delete cart[productId];
  saveCart(cart);
}

function cartDetails() {
  const cart = getCart();
  let items = [];
  let subtotal = 0;
  for (let pid in cart) {
    const prod = PRODUCTS.find(p => p.id === pid);
    if (!prod) continue;
    const qty = Number(cart[pid]);
    const total = +(prod.price * qty);
    items.push({ ...prod, qty, total });
    subtotal += total;
  }
  return { items, subtotal: +subtotal.toFixed(2), shipping: subtotal > 0 ? 5.00 : 0, tax: +(subtotal * 0.10).toFixed(2), grandTotal: +(subtotal + (subtotal > 0 ? 5 : 0) + +(subtotal * 0.10)).toFixed(2) };
}

function loginUser(email) {
  localStorage.setItem('user', JSON.stringify({ email }));
}
function getUser() {
  try { return JSON.parse(localStorage.getItem('user') || 'null'); } catch (e) { return null; }
}
function logoutUser() {
  localStorage.removeItem('user');
}

function updateCartIndicator() {
  const el = document.querySelector('#cart-count');
  if (!el) return;
  const cart = getCart();
  const qty = Object.values(cart).reduce((s, n) => s + Number(n), 0);
  el.textContent = qty > 0 ? qty : '';
}

function fmt(v) { return '₹' + v.toFixed(2); }

function renderProductsGrid(containerSelector) {
  const container = document.querySelector(containerSelector);
  if (!container) return;
  container.innerHTML = '';
  const grid = document.createElement('div'); grid.className = 'products-grid';
  PRODUCTS.forEach(p => {
    const card = document.createElement('div'); card.className = 'card';
    card.innerHTML = `
      <img src="${p.img}" alt="${p.title}">
      <div class="title">${p.title}</div>
      <div class="desc">${p.desc}</div>
      <div class="row" style="justify-content:space-between;align-items:center;">
        <div class="price">${fmt(p.price)}</div>
        <div style="display:flex;gap:8px;">
          <button class="btn" data-add="${p.id}">Add</button>
          <a class="btn secondary" href="javascript:void(0)" data-view="${p.id}">View</a>
        </div>
      </div>
    `;
    grid.appendChild(card);
  });
  container.appendChild(grid);

  container.querySelectorAll('[data-add]').forEach(btn => {
    btn.addEventListener('click', ev => {
      const id = ev.currentTarget.getAttribute('data-add');
      addToCart(id, 1);
      updateCartIndicator();

      btn.textContent = 'Added ✓';
      setTimeout(() => btn.textContent = 'Add', 900);
    });
  });
  container.querySelectorAll('[data-view]').forEach(btn => {
    btn.addEventListener('click', ev => {
      const id = ev.currentTarget.getAttribute('data-view');

      window.location.href = 'cart.html';
    });
  });
}

function renderCartPage() {
  const listContainer = document.querySelector('#cart-list');
  const summaryContainer = document.querySelector('#cart-summary');
  if (!listContainer || !summaryContainer) return;
  const details = cartDetails();
  listContainer.innerHTML = '';
  if (details.items.length === 0) {
    listContainer.innerHTML = `<div class="form center">Your cart is empty. <a href="index.html">Shop products</a></div>`;
    summaryContainer.innerHTML = '';
    updateCartIndicator();
    return;
  }
  details.items.forEach(it => {
    const node = document.createElement('div'); node.className = 'cart-item';
    node.innerHTML = `
      <img src="${it.img}" alt="${it.title}">
      <div style="flex:1;">
        <div style="font-weight:700">${it.title}</div>
        <div class="small text-muted">${it.desc}</div>
        <div class="row" style="margin-top:8px;">
          <div class="price">${fmt(it.price)}</div>
          <div style="margin-left:auto;">
            <input type="number" min="0" class="qty-input" data-pid="${it.id}" value="${it.qty}">
            <button class="btn secondary" data-remove="${it.id}">Remove</button>
          </div>
        </div>
      </div>
    `;
    listContainer.appendChild(node);
  });

  summaryContainer.innerHTML = `
    <div class="form">
      <div style="font-weight:700">Order Summary</div>
      <div class="small">Subtotal: ${fmt(details.subtotal)}</div>
      <div class="small">Shipping: ${fmt(details.shipping)}</div>
      <div class="small">Tax (10%): ${fmt(details.tax)}</div>
      <hr style="margin:12px 0;">
      <div style="font-weight:800; font-size:16px;">Total: ${fmt(details.grandTotal)}</div>
      <div style="margin-top:12px;">
        <a class="btn" href="checkout.html">Proceed to Checkout</a>
      </div>
    </div>
  `;

  listContainer.querySelectorAll('.qty-input').forEach(inp => {
    inp.addEventListener('change', e => {
      const pid = e.currentTarget.getAttribute('data-pid');
      let q = Number(e.currentTarget.value) || 0;
      if (q <= 0) {
        removeFromCart(pid);
        e.currentTarget.parentElement.parentElement.parentElement.remove();
      } else {
        setCartQty(pid, q);
      }

      renderCartPage();
      updateCartIndicator();
    });
  });
  listContainer.querySelectorAll('[data-remove]').forEach(btn => {
    btn.addEventListener('click', e => {
      const pid = e.currentTarget.getAttribute('data-remove');
      removeFromCart(pid);
      renderCartPage();
      updateCartIndicator();
    });
  });
}

function renderCheckoutPage() {
  const summaryBox = document.querySelector('#checkout-summary');
  const form = document.querySelector('#checkout-form');
  if (!summaryBox || !form) return;
  const details = cartDetails();
  if (details.items.length === 0) {
    summaryBox.innerHTML = `<div class="form center">Cart is empty. <a href="index.html">Add products</a></div>`;
    form.style.display = 'none';
    return;
  }
  summaryBox.innerHTML = `
    <div class="form">
      <div style="font-weight:700">Order Summary</div>
      ${details.items.map(it => `<div style="display:flex;justify-content:space-between;margin-top:6px;"><div>${it.title} x ${it.qty}</div><div>${fmt(it.total)}</div></div>`).join('')}
      <hr style="margin:12px 0;">
      <div class="small">Subtotal: ${fmt(details.subtotal)}</div>
      <div class="small">Shipping: ${fmt(details.shipping)}</div>
      <div class="small">Tax: ${fmt(details.tax)}</div>
      <div style="font-weight:800; margin-top:10px;">Total: ${fmt(details.grandTotal)}</div>
    </div>
  `;

  form.addEventListener('submit', (ev) => {
    ev.preventDefault();

    const data = new FormData(form);
    const name = data.get('name')?.trim();
    const email = data.get('email')?.trim();
    const address = data.get('address')?.trim();
    const city = data.get('city')?.trim();
    const zipcode = data.get('zipcode')?.trim();
    const payment = data.get('payment');

    if (!name || !email || !address || !city || !zipcode || !payment) {
      alert('Please complete all required fields.');
      return;
    }

    if (!/^\S+@\S+\.\S+$/.test(email)) {
      alert('Please enter a valid email.');
      return;
    }

    const order = {
      id: 'ORD' + Date.now(),
      created: new Date().toISOString(),
      items: details.items,
      total: details.grandTotal,
      shippingAddress: { name, address, city, zipcode },
      email,
      payment
    };

    localStorage.setItem('lastOrder', JSON.stringify(order));
    clearCart();
    updateCartIndicator();

    window.location.href = 'checkout.html?success=1';
  });
}

function showCheckoutConfirmation() {
  const url = new URL(window.location.href);
  if (url.searchParams.get('success') === '1') {
    const order = JSON.parse(localStorage.getItem('lastOrder') || '{}');
    const box = document.querySelector('#checkout-confirmation');
    if (!box) return;
    box.innerHTML = `
      <div class="form center">
        <div style="font-weight:700; font-size:18px;">Order placed — ${order.id}</div>
        <div class="small" style="margin-top:8px;">A confirmation has been sent to <strong>${order.email}</strong></div>
        <div style="margin-top:12px;">
          <a class="btn" href="index.html">Continue Shopping</a>
        </div>
      </div>
    `;
  }
}

function setupHeader() {
  const user = getUser();
  const loginLink = document.querySelector('#nav-login');
  const userDisplay = document.querySelector('#nav-user');
  const logoutBtn = document.querySelector('#nav-logout');
  if (user) {
    if (loginLink) loginLink.style.display = 'none';
    if (userDisplay) { userDisplay.style.display = 'inline-block'; userDisplay.textContent = user.email; }
    if (logoutBtn) logoutBtn.style.display = 'inline-block';
  } else {
    if (loginLink) loginLink.style.display = 'inline-block';
    if (userDisplay) userDisplay.style.display = 'none';
    if (logoutBtn) logoutBtn.style.display = 'none';
  }
  if (logoutBtn) {
    logoutBtn.addEventListener('click', () => {
      logoutUser(); setupHeader(); window.location.href = 'index.html';
    });
  }
  updateCartIndicator();
}
document.addEventListener('DOMContentLoaded', () => {
  setupHeader();
  if (document.querySelector('#products-root')) renderProductsGrid('#products-root');

  if (document.querySelector('#cart-list')) renderCartPage();

  if (document.querySelector('#checkout-summary')) renderCheckoutPage();
  if (document.querySelector('#checkout-confirmation')) showCheckoutConfirmation();
});
